import { Span } from 'app/components/Typography';
import { useEffect, useState } from 'react';
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator';
import * as React from 'react';
import frLocale from 'date-fns/locale/fr';
import ruLocale from 'date-fns/locale/ru';
import deLocale from 'date-fns/locale/de';
import enLocale from 'date-fns/locale/en-US';
import arSaLocale from 'date-fns/locale/ar-SA';
import Stack from '@mui/material/Stack';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import TextField from '@mui/material/TextField';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { Button, Grid, Icon, styled } from '@mui/material';
import { inputFormElements } from 'app/components/FormElements';

const localeMap = {
  en: enLocale,
  fr: frLocale,
  de: deLocale,
  ru: ruLocale,
  ar: arSaLocale,
};

// const TextField = styled(TextValidator)(() => ({
//   width: '100%',
//   marginBottom: '16px',
// }));

const ProjectAddFrom = () => {
  const [state, setState] = useState({ date: new Date() });
  const [locale, setLocale] = React.useState('ru');
  const [datePickerValue, setDatePickerValue] = React.useState(new Date());

  const [timePickerValue, setTimePickerValue] = React.useState(new Date());

  const selectLocale = (newLocale) => {
    setLocale(newLocale);
  };

  const handleSubmit = (event) => {
    // console.log("submitted");
    console.log(event);
  };

  const handleChange = (event) => {
    event.persist();
    setState({ ...state, [event.target.name]: event.target.value });
  };

  const { projectname, date } = state;

  return (
    <div>
      <ValidatorForm onSubmit={handleSubmit} onError={() => null}>
        <Grid style={{ padding: '80px 5px 0 5px' }}>
          <Card style={{ maxWidth: 600, margin: '0 auto' }}>
            <CardContent>
              <Typography variant="h4" color="primary">
                Create an Account
              </Typography>
              <Typography variant="subtitle1" color="textSecondary">
                Fill all the mandatory fields to create an account.
              </Typography>
              <form>
                <Typography variant="body2" align="left" gutterBottom>
                  Personal Info :{' '}
                </Typography>
                <Grid container spacing={1}>
                  {inputFormElements.slice(0, 4).map((input) => (
                    <Grid xs={input.xs} sm={input.sm} item>
                      <TextField {...input} />
                    </Grid>
                  ))}
                </Grid>
                <Typography variant="body2" align="left" gutterBottom>
                  Address :{' '}
                </Typography>
                <Grid container spacing={1}>
                  {inputFormElements.slice(4, 9).map((input) => (
                    <Grid xs={input.xs} sm={input.sm} item>
                      <TextField {...input} />
                    </Grid>
                  ))}
                </Grid>
                <Grid container spacing={1}>
                  <Grid item xs={12} align="right">
                    <Button style={margin} type="reset" variant="outlined" color="primary">
                      Reset
                    </Button>
                    <Button type="submit" variant="contained" color="primary">
                      Submit
                    </Button>
                  </Grid>
                </Grid>
              </form>
            </CardContent>
          </Card>
        </Grid>
      </ValidatorForm>
    </div>
  );
};

export default ProjectAddFrom;
