import { Span } from 'app/components/Typography';
import { useEffect, useState } from 'react';
import { TextValidator, ValidatorForm } from 'react-material-ui-form-validator';
import * as React from 'react';
import frLocale from 'date-fns/locale/fr';
import ruLocale from 'date-fns/locale/ru';
import deLocale from 'date-fns/locale/de';
import enLocale from 'date-fns/locale/en-US';
import arSaLocale from 'date-fns/locale/ar-SA';
import Stack from '@mui/material/Stack';
import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import TextField from '@mui/material/TextField';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { TimePicker } from '@mui/x-date-pickers/TimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import {
  Button,
  Checkbox,
  FormControlLabel,
  Grid,
  Icon,
  Radio,
  RadioGroup,
  styled,
} from '@mui/material';

const localeMap = {
  en: enLocale,
  fr: frLocale,
  de: deLocale,
  ru: ruLocale,
  ar: arSaLocale,
};

// const TextField = styled(TextValidator)(() => ({
//   width: '100%',
//   marginBottom: '16px',
// }));

const ProjectAddFrom = () => {
  const [state, setState] = useState({ date: new Date() });
  const [locale, setLocale] = React.useState('ru');
  const [datePickerValue, setDatePickerValue] = React.useState(new Date());

  const [timePickerValue, setTimePickerValue] = React.useState(new Date());

  const selectLocale = (newLocale) => {
    setLocale(newLocale);
  };
  useEffect(() => {
    ValidatorForm.addValidationRule('isPasswordMatch', (value) => {
      if (value !== state.password) return false;

      return true;
    });
    return () => ValidatorForm.removeValidationRule('isPasswordMatch');
  }, [state.password]);

  const handleSubmit = (event) => {
    // console.log("submitted");
    // console.log(event);
  };

  const handleChange = (event) => {
    event.persist();
    setState({ ...state, [event.target.name]: event.target.value });
  };

  const handleDateChange = (date) => setState({ ...state, date });

  const { projectname, date } = state;

  return (
    <div>
      <ValidatorForm onSubmit={handleSubmit} onError={() => null}>
        <Grid container spacing={6}>
          <Grid item lg={6} md={6} sm={12} xs={12} sx={{ mt: 2 }}>
            <TextField
              type="text"
              name="projectname"
              id="standard-basic"
              value={projectname || ''}
              onChange={handleChange}
              errorMessages={['this field is required']}
              label="Username (Min length 4, Max length 9)"
              validators={['required', 'minStringLength: 4', 'maxStringLength: 9']}
            />

            <LocalizationProvider dateAdapter={AdapterDateFns} adapterLocale={localeMap[locale]}>
              <Stack spacing={3}>
                <ToggleButtonGroup value={locale} exclusive sx={{ mb: 2, display: 'block' }}>
                  {Object.keys(localeMap).map((localeItem) => (
                    <ToggleButton
                      key={localeItem}
                      value={localeItem}
                      onClick={() => selectLocale(localeItem)}
                    >
                      {localeItem}
                    </ToggleButton>
                  ))}
                </ToggleButtonGroup>
                <DatePicker
                  value={datePickerValue}
                  onChange={(newValue) => setDatePickerValue(newValue)}
                  renderInput={(params) => <TextField {...params} />}
                />
                <TimePicker
                  value={timePickerValue}
                  onChange={(newValue) => setTimePickerValue(newValue)}
                  renderInput={(params) => <TextField {...params} />}
                />
              </Stack>
            </LocalizationProvider>
          </Grid>
        </Grid>
        <Button color="primary" variant="contained" type="submit">
          <Icon>send</Icon>
          <Span sx={{ pl: 1, textTransform: 'capitalize' }}>Save Project</Span>
        </Button>
      </ValidatorForm>
    </div>
  );
};

export default ProjectAddFrom;
