import React, { useState, useEffect } from 'react';
import { ThemeProvider, createTheme, List, ListItemText } from '@mui/material';
import MUIDataTable from 'mui-datatables';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
  FormLabel,
  FormGroup,
} from '@mui/material';

const data = [
  {
    Address: 'France',
    firstName: 'Anna',
    cartItems: [
      {
        color: 'Black',
        size: 20,
        quantity: 2,
        name: 'Item 1',
      },
    ],
  },
  {
    firstName: 'Anna',
    Address: 'US',
    cartItems: [
      {
        size: '500',
        color: 'Green',
        name: 'Item 1',
        quantity: 2,
      },
      {
        name: 'Tumbler',
        color: 'Black',

        size: '500',
        quantity: 1,
      },
      {
        color: 'Pink',
        quantity: 1,

        size: '500',
        name: 'Tumbler',
      },
      {
        size: 'XL',
        color: 'Pink',

        quantity: 1,
        name: 'Shirt',
      },
      {
        size: 'XL',
        color: 'Green',

        name: 'Shirt',
        quantity: 1,
        price: 500,
      },
    ],
  },
];

const Test = () => {
  //   const [data, setData] = useState();
  //   useEffect(() => {
  //     const url = 'https://jsonplaceholder.typicode.com/posts';
  //     const getDatas = fetch(url)
  //       .then((res) => res.json())
  //       .then((data) => {
  //         // this.setState({ data: data });
  //         setData(data);
  //       })
  //       .catch((err) => console.log('error:', err));
  //   }, [setData]);
  const columns = [
    {
      name: 'name',
      label: 'Name',
      options: {
        filter: true,
      },
    },
    {
      name: 'cpountry',
      label: 'Country',
      options: {
        filter: true,
      },
    },
    {
      name: 'items',
      label: 'Items',
      options: {
        filter: true,
      },
    },
    {
      name: 'cartItems',
      label: 'Items',
      options: {
        filter: true,
        sort: true,
        customBodyRender: (value, tableMeta, updateValue) => {
          return Object.entries(
            value.reduce((prev, item) => {
              if (!prev[item.id]) prev[item.id] = { ...item, nest: [] };
              prev[item.id].nest.push(item);
              return prev;
            }, {})
          );
          // .map(([id, obj], idx) => (
          //   <ul key={id + obj.color}>
          //     <ListItemText primary={obj.name + ' ' + obj.size} />

          //     <li>
          //       {obj.nest.map((nest, idx) => (
          //         <li key={idx}>
          //           <ListItemText secondary={nest.color + ' (' + nest.quantity + ' pcs)'} />
          //         </li>
          //       ))}
          //     </li>
          //   </ul>
          // ));
        },
      },
    },
  ];

  const options = {
    filter: true,
    selectableRows: 'none',
    responsive: 'scrollMaxHeight',
    expandableRows: true,
    renderExpandableRow: (rowData, rowMeta) => {
      console.log(rowData);
      return (
        <tr>
          <td colSpan={4}>
            <TableContainer>
              <Table style={{ margin: '0 auto' }}>
                <TableHead>
                  <TableCell align="right">Name</TableCell>
                  <TableCell align="right">Color</TableCell>
                  <TableCell align="right">Size</TableCell>
                </TableHead>
                <TableBody>
                  {rowData[3].map((row) => {
                    console.log(row);
                    console.log(rowData);
                    return (
                      <TableRow key={row.id}>
                        <TableCell component="th" scope="row" align="right">
                          {row.name}
                        </TableCell>
                        <TableCell align="right">{row.color}</TableCell>
                        <TableCell align="right">{row.size}</TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>
          </td>
        </tr>
      );
    },
  };
  return (
    <div className="App">
      <ThemeProvider theme={createTheme()}>
        {/* total amount of the current page: {total} */}
        <MUIDataTable title={'Participants'} options={options} columns={columns} data={data} />
      </ThemeProvider>
    </div>
  );
};

export default Test;
