import React, { useRef, useEffect } from 'react';
import MUIDataTable from 'mui-datatables';
import Done from '@material-ui/icons/Done';
import Error from '@material-ui/icons/Error';
import Tooltip from '@material-ui/core/Tooltip';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Button from '@material-ui/core/Button';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogContentText from '@mui/material/DialogContentText';
import DialogTitle from '@mui/material/DialogTitle';

const AppUsersForms = ({ showModal, setShowModal }) => {
  const handleClose = () => {
    setShowModal((prev) => !prev);
  };

  const descriptionElementRef = useRef(null);
  useEffect(() => {
    if (showModal) {
      const { current: descriptionElement } = descriptionElementRef;
      if (descriptionElement !== null) {
        descriptionElement.focus();
      }
    }
  }, [showModal]);

  const columns = [
    { name: 'Deployment ID' },
    { name: 'Application' },
    'Configuration',
    'Version ',
    'Last Deployed',
    'Approval',
    'Operation',
    {
      name: 'Health',
      options: {
        customBodyRender: (value, tableMeta, updateValue) => {
          if (value === 'OK')
            return (
              <Tooltip title="OK">
                <Done color="primary" />
              </Tooltip>
            );
          else
            return (
              <Tooltip title="Failing">
                <Error color="error" />
              </Tooltip>
            );
        },
      },
    },
    {
      name: 'Actions',
      options: {
        customBodyRender: (value, tableMeta, updateValue) => {
          return (
            <Button variant="outlined" color="secondary">
              {`Review`}
            </Button>
          );
        },
      },
    },
  ];

  const data = [
    [
      '78r9-2d34-asd2342fxsdfs3',
      'kelaForecast',
      'Production',
      1.5,
      '10/28/2018',
      'Approved',
      'Complete',
      'OK',
      '',
    ],
    [
      '78r9-2d34-asd2342fxsdfs3',
      'kelaForecast',
      'Test',
      1.5,
      '10/28/2018',
      'New Version Pending',
      'Error',
      'Failing',
      'Bad',
    ],
    [
      '78r9-2d34-asd2342fxsdfs3',
      'InnerVision',
      'Production',
      2.0,
      '10/31/2018',
      'Approved',
      'Complete',
      'OK',
      '',
    ],
    [
      '78r9-2d34-asd2342fxsdfs3',
      'InnerVision',
      'Test',
      2.1,
      '10/31/2018',
      'Approved',
      'In Progress',
      'OK',
      '',
    ],
    [
      '78r9-2d34-asd2342fxsdfs3',
      'OncoKnowledge',
      'Production',
      1.0,
      '10/29/2018',
      'Approved',
      'Complete',
      'OK',
      '',
    ],
    [
      '78r9-2d34-asd2342fxsdfs3',
      'OncoKnowledge',
      'Test',
      1.12,
      '10/28/2018',
      'New Version Pending',
      'Error',
      'Failing',
      'Bad',
    ],
  ];

  const options = {
    filterType: 'dropdown',
    responsive: 'scroll',
    selectableRows: true,
  };

  return (
    <Dialog
      open={showModal}
      onClose={handleClose}
      scroll="paper"
      aria-labelledby="scroll-dialog-title"
      aria-describedby="scroll-dialog-description"
    >
      <DialogTitle id="scroll-dialog-title">Add Participants</DialogTitle>
      <DialogContent dividers>
        <DialogContentText id="scroll-dialog-description" ref={descriptionElementRef} tabIndex={-1}>
          <MUIDataTable title={'Deployments'} data={data} columns={columns} options={options} />
        </DialogContentText>
      </DialogContent>
    </Dialog>
  );
};

export default AppUsersForms;
