import React, { useState } from 'react';
import { ThemeProvider, createTheme, List, ListItemText } from '@mui/material';
import MUIDataTable from 'mui-datatables';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TextField,
  FormLabel,
  FormGroup,
} from '@mui/material';

const data = [
  {
    cartItems: [
      {
        color: 'Black',
        size: 20,
        quantity: 2,
        name: 'Item 1',
      },
    ],
    Address: 'France',
    firstName: 'Anna',
  },
  {
    firstName: 'Anna',
    cartItems: [
      {
        size: '500',
        color: 'Green',
        name: 'Item 1',
        quantity: 2,
      },
      {
        name: 'Tumbler',
        color: 'Black',

        size: '500',
        quantity: 1,
      },
      {
        color: 'Pink',
        quantity: 1,

        size: '500',
        name: 'Tumbler',
      },
      {
        size: 'XL',
        color: 'Pink',

        quantity: 1,
        name: 'Shirt',
      },
      {
        size: 'XL',
        color: 'Green',

        name: 'Shirt',
        quantity: 1,
        price: 500,
      },
    ],
    Address: 'US',
  },
];

const test = () => {
  const [data, setData] = useState();
  useEffect(() => {
    const url = 'https://jsonplaceholder.typicode.com/posts';
    const getDatas = fetch(url)
      .then((res) => res.json())
      .then((data) => {
        // this.setState({ data: data });
        setData(data);
      })
      .catch((err) => console.log('error:', err));
  }, [setData]);
  const columns = [
    {
      name: 'id',
      label: 'System ID', //or the order ID here
      options: {
        filter: true,
        sort: true,
        display: false,
      },
    },

    {
      name: 'firstName',
      label: 'Name',
      options: {
        filter: true,
        sort: true,
      },
    },

    {
      name: 'Address',
      label: 'Country',
      options: {
        filter: true,
        sort: true,
      },
    },
    {
      name: 'cartItems',
      label: 'Items 1',
      options: {
        filter: false,
        sort: false,
        display: false,
        viewColumns: false,
      },
    },
    {
      name: 'cartItems',
      label: 'Items',
      options: {
        filter: true,
        sort: true,
        customBodyRender: (value, tableMeta, updateValue) => {
          return Object.entries(
            value.reduce((prev, item) => {
              if (!prev[item.id]) prev[item.id] = { ...item, nest: [] };
              prev[item.id].nest.push(item);
              return prev;
            }, {})
          );
          //   .map(([id, obj], idx) => (
          //     <ul key={id + obj.color}>
          //       <ListItemText primary={obj.name + ' ' + obj.size} />

          //       <li>
          //         {obj.nest.map((nest, idx) => (
          //           <li key={idx}>
          //             <ListItemText secondary={nest.color + ' (' + nest.quantity + ' pcs)'} />
          //           </li>
          //         ))}
          //       </li>
          //     </ul>
          //   ));
        },
      },
    },
  ];

  const options = {
    filter: true,
    selectableRows: 'none',
    responsive: 'scrollMaxHeight',
    expandableRows: true,
    renderExpandableRow: (rowData, rowMeta) => {
      console.log(rowData);
      return (
        <tr>
          <td colSpan={4}>
            <TableContainer>
              <Table style={{ margin: '0 auto' }}>
                <TableHead>
                  <TableCell align="right">Body</TableCell>
                  {/* <TableCell align="right">Color</TableCell>
                  <TableCell align="right">Size</TableCell> */}
                </TableHead>
                <TableBody>
                  {rowData[3].map((row) => {
                    console.log(row);
                    return (
                      <TableRow key={row.id}>
                        <TableCell component="th" scope="row" align="right">
                          {row.body}
                        </TableCell>
                        {/* <TableCell align="right">{row.color}</TableCell>
                        <TableCell align="right">{row.size}</TableCell> */}
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>
          </td>
        </tr>
      );
    },
  };
  return (
    <div className="App">
      <ThemeProvider theme={createTheme()}>
        {/* total amount of the current page: {total} */}
        <MUIDataTable title={'Participants'} options={options} columns={columns} data={data} />
      </ThemeProvider>
    </div>
  );
};

export default test;
