      phone: 9876543210,
      age: 20,
      gender: 'M',
      city: 'Mumbai',
      fee: 874569,
    },
  ]);
  const columns = [
    {
      title: 'Name',
      field: 'name',
      sorting: false,
      filtering: false,
      cellStyle: { background: '#009688' },
      headerStyle: { color: '#fff' },
    },
    { title: 'Email', field: 'email', filterPlaceholder: 'filter' },
    { title: 'Phone Number', field: 'phone', align: 'center', grouping: false },
    {
      title: 'Age',
      field: 'age',
      emptyValue: () => <em>null</em>,
      render: (rowData) => (
        <div
          style={{
            background: rowData.age >= 18 ? '#008000aa' : '#f90000aa',
            borderRadius: '4px',
            paddingLeft: 5,
          }}
        >
          {rowData.age >= 18 ? '18+' : '18-'}
        </div>
      ),
      searchable: false,
      export: false,
    },
    { title: 'Gender', field: 'gender', lookup: { M: 'Male', F: 'Female' } },
    { title: 'City', field: 'city', filterPlaceholder: 'filter' },
    {
      title: 'School Fee',
      field: 'fee',
      type: 'currency',
      currencySetting: { currencyCode: 'INR', minimumFractionDigits: 1 },
      cellStyle: { background: '#009688' },
      headerStyle: { color: '#fff' },
    },
  ];
  return (
    <Container>
      <Box className="breadcrumb">
        <Breadcrumb routeSegments={[{ name: 'Partners', path: '/records' }, { name: 'Records' }]} />
      </Box>
      <MaterialTable columns={columns} data={tableData} />
    </Container>
  );
};

export default AppTable;
