import React, { useState } from 'react';
import { Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/styles';
import MUIDataTable from 'mui-datatables';
import { Box, styled, Icon } from '@mui/material';
import { Breadcrumb } from 'app/components';
import Button from '@mui/material/Button';
import { PopUp } from 'app/components';
import ParticipantsForms from './ParticipantsForms';
import * as employeeService from '../../services/employeeService';
import Controls from '../../components/controls/Controls';
import AddIcon from '@material-ui/icons/Add';

const datatableData = [
  ['Joe James', 'Example Inc.', 'Yonkers', 'NY'],
  ['John Walsh', 'Example Inc.', 'Hartford', 'CT'],
  ['Bob Herm', 'Example Inc.', 'Tampa', 'FL'],
  ['James Houston', 'Example Inc.', 'Dallas', 'TX'],
  ['Prabhakar Linwood', 'Example Inc.', 'Hartford', 'CT'],
  ['Kaui Ignace', 'Example Inc.', 'Yonkers', 'NY'],
  ['Esperanza Susanne', 'Example Inc.', 'Hartford', 'CT'],
  ['Christian Birgitte', 'Example Inc.', 'Tampa', 'FL'],
  ['Meral Elias', 'Example Inc.', 'Hartford', 'CT'],
  ['Deep Pau', 'Example Inc.', 'Yonkers', 'NY'],
  ['Sebastiana Hani', 'Example Inc.', 'Dallas', 'TX'],
  ['Marciano Oihana', 'Example Inc.', 'Yonkers', 'NY'],
  ['Brigid Ankur', 'Example Inc.', 'Dallas', 'TX'],
  ['Anna Siranush', 'Example Inc.', 'Yonkers', 'NY'],
  ['Avram Sylva', 'Example Inc.', 'Hartford', 'CT'],
  ['Serafima Babatunde', 'Example Inc.', 'Tampa', 'FL'],
  ['Gaston Festus', 'Example Inc.', 'Tampa', 'FL'],
];

const useStyles = makeStyles((theme) => ({
  tableOverflow: {
    overflow: 'auto',
  },
  addButton: {
    position: 'absolute',
    right: '25px',
  },
}));

const Container = styled('div')(({ theme }) => ({
  margin: '30px',
  [theme.breakpoints.down('sm')]: { margin: '16px' },
  '& .breadcrumb': {
    marginBottom: '30px',
    [theme.breakpoints.down('sm')]: { marginBottom: '16px' },
  },
  '& .addButton': {
    marginBottom: '30px',
    [theme.breakpoints.down('sm')]: { marginBottom: '16px' },
  },
}));

const options = {
  onRowsDelete: (rowsDeleted, dataRows) => {
    console.log(dataRows[0]);
    console.log(rowsDeleted.data);
  },
};

const ParticipantsTable = () => {
  const classes = useStyles();
  const [openPopup, setOpenPopup] = useState(false);
  const [recordForEdit, setRecordForEdit] = useState(null);

  const addOrEdit = (employee, resetForm) => {
    if (employee.id == 0) employeeService.insertEmployee(employee);
    else employeeService.updateEmployee(employee);
    resetForm();
    setRecordForEdit(null);
    setOpenPopup(false);
  };

  const openInPopup = (item) => {
    setRecordForEdit(item);
    setOpenPopup(true);
  };

  return (
    <Container>
      <Box className="breadcrumb" display="flex">
        <Breadcrumb
          routeSegments={[{ name: 'Participants', path: '/records' }, { name: 'Records' }]}
        />{' '}
        {/* <Button
          variant="contained"
          color="success"
          className={classes.addButton}
          onClick={() => {
            setOpenPopup(true);
            setRecordForEdit(null);
          }}
        > */}
        <Controls.Button
          text="Add New"
          variant="outlined"
          startIcon={<AddIcon />}
          className={classes.addButton}
          onClick={() => {
            setOpenPopup(true);
            setRecordForEdit(null);
          }}
        />
        <Icon>add</Icon>
        <span>Participants</span>
        {/* </Button> */}
      </Box>
      {/* <Box display="flex"> */}

      {/* </Box> */}

      <Grid container spacing={4}>
        <Grid item xs={12}>
          <MUIDataTable
            title="Participants List"
            data={datatableData}
            columns={['Name', 'Company', 'City', 'State']}
            options={options}
          />
        </Grid>
      </Grid>
      <PopUp title="Employee Form" openPopup={openPopup} setOpenPopup={setOpenPopup}>
        <ParticipantsForms recordForEdit={recordForEdit} addOrEdit={addOrEdit} />
      </PopUp>
    </Container>
  );
};

export default ParticipantsTable;
